import React from "react";

let dropmenuMaakunnatIsVisible = false;
let dropmenuTyypitIsVisible = false;

class Valikot extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      valittuMaakunta: "",
      valittuTyyppi: "",
    };

    this.valitseMaakunta = this.valitseMaakunta.bind(this);
    this.valitseTyyppi = this.valitseTyyppi.bind(this);
  }
  luoMaakuntaValikko() {
    let selectBox = document.getElementById("inputMaakunnat");
    const options = Object.values(this.props.maakunnat).map((maakunta) => (
      <p
        className="valinnat"
        onClick={(e) => {
          selectBox.value = e.target.innerHTML;
          this.setState({ maakunta: e.target.innerHTML });
          selectBox.focus();
          document.getElementById("maakunnatContainer").style.display = "none";
          dropmenuMaakunnatIsVisible = false;
          this.valitseMaakunta(selectBox.value);
        }}
      >
        {maakunta}
      </p>
    ));

    return (
      <div
        id={"maakunnatContainer"}
        className="options"
        style={{ display: "none" }}
      >
        {options}
      </div>
    );
  }

  luoTyypitValikko() {
    let selectBox = document.getElementById("inputTyypit");
    const options = Object.values(this.props.tyypit).map((tyyppi) => (
      <p
        className="valinnat"
        onClick={(e) => {
          selectBox.value = e.target.innerHTML;
          this.setState({ tyyppi: e.target.innerHTML });
          selectBox.focus();
          document.getElementById("tyypitContainer").style.display = "none";
          dropmenuTyypitIsVisible = false;
          this.valitseTyyppi(selectBox.value);
        }}
      >
        {tyyppi}
      </p>
    ));

    return (
      <div
        id={"tyypitContainer"}
        className="options"
        style={{ display: "none" }}
      >
        {options}
      </div>
    );
  }

  naytaMaakunnat() {
    //document.getElementById("container").style.display = "initial";
    let valikko = document.getElementById("maakunnatContainer");

    dropmenuMaakunnatIsVisible = !dropmenuMaakunnatIsVisible;
    if (dropmenuMaakunnatIsVisible) {
      valikko.style.display = "block";
    } else {
      valikko.style.display = "none";
    }
  }

  naytaTyypit() {
    let valikko = document.getElementById("tyypitContainer");

    dropmenuTyypitIsVisible = !dropmenuTyypitIsVisible;
    if (dropmenuTyypitIsVisible) {
      valikko.style.display = "block";
    } else {
      valikko.style.display = "none";
    }
  }

  valitseMaakunta(maakunta) {
    this.setState({ valittuMaakunta: maakunta }, () =>
      console.log("muutettu maakunta:", this.state.valittuMaakunta)
    );
  }

  valitseTyyppi(tyyppi) {
    this.setState({ valittuTyyppi: tyyppi }, () =>
      console.log("muutettu tyyppi:", this.state.valittuTyyppi)
    );
  }

  render() {
    return (
      <div
        maakunta={this.state.valittuMaakunta}
        tyyppi={this.state.valittuTyyppi}
      >
        <input
          id={"inputMaakunnat"}
          type={"text"}
          onClick={() => this.naytaMaakunnat()}
          placeholder={"Valitse maakunta"}
          readOnly
        ></input>

        {this.luoMaakuntaValikko()}

        <input
          id={"inputTyypit"}
          type={"text"}
          onClick={() => {
            this.naytaTyypit();
          }}
          placeholder={"Valitse tyyppi"}
          readOnly
        ></input>

        {this.luoTyypitValikko()}
      </div>
    );
  }
}

export default Valikot;
