import React, { useState, useEffect } from "react";
import ReactMapGl, { Marker, Popup } from "react-map-gl";
const access_token =
  "pk.eyJ1Ijoic2F1bGlrIiwiYSI6ImNrcWM4NXJoYzAwdzkybm56djVka2VzZW0ifQ.QmYDZh6DNEwu5ITY9lQ6Ag";

function Map(props) {
  const [viewport, setViewport] = useState({
    latitude: 62,
    longitude: 25,
    zoom: 5,
  });

  const [paikat, setPaikat] = useState([]);

  const addMarkers = () => {};

  if (props.maakunta && props.tyyppi) {
    let rajatutPaikat = [];
    async function f() {
      {
        rajatutPaikat = props.data.filter(
          (paikka) =>
            paikka.maakunta == props.maakunta && paikka.tyyppi == props.tyyppi
        );
      }
    }

    f().then(setPaikat([rajatutPaikat]));
  }

  const paivitaPaikat = (arr) => {
    setPaikat([...arr]);
  };

  console.log("map rendered");
  /* console.log("Mapissa maakunta: ", props.maakunta);
  console.log("Mapissa tyyppi: ", props.tyyppi);
  console.log("Mapissa paikat: ", props.data); */

  return (
    <ReactMapGl
      className="map"
      {...viewport}
      mapboxApiAccessToken={access_token}
      mapStyle="mapbox://styles/mapbox/streets-v11"
      height={800}
      width={600}
      onViewportChange={(viewport) => {
        setViewport(viewport);
      }}
    ></ReactMapGl>
  );
}

export default Map;
