import React, { useState, useEffect, useRef } from "react";
import ReactMapGl, { Marker, Popup, TRANSITION_EVENTS } from "react-map-gl";
import { useTransition, animated, useSpring } from "react-spring";
import { TransitionGroup, CSSTransition } from "react-transition-group";
import icon from "./red_pin.png";
import "./styles.css";

const access_token =
  "pk.eyJ1Ijoic2F1bGlrIiwiYSI6ImNrcWM4NXJoYzAwdzkybm56djVka2VzZW0ifQ.QmYDZh6DNEwu5ITY9lQ6Ag";

function Map(props) {
  const [viewport, setViewport] = useState({
    latitude: 62,
    longitude: 25,
    zoom: 5,
    transitionDuration: 500,
  });

  const [paikat, setPaikat] = useState(props.rajatutPaikat);
  //  const [kaikkiPaikat, setKaikkiPaikat] = useState(props.kaikkiPaikat);
  const [valittuMarker, setValittuMarker] = useState();
  /*  const prevLat = useRef(props.newLat);
  const prevLong = useRef(props.newLong); */
  const [lat, setLat] = useState();
  const [long, setLong] = useState();
  const styles = useSpring({
    loop: true,
    to: [{ opacity: 1 }, { opacity: 0 }],
    from: { opacity: 0 },
  });

  useEffect(() => {
    setPaikat(props.rajatutPaikat);
    setValittuMarker(null);
  }, [props.rajatutPaikat]);

  useEffect(() => {
    if (props.newLat || props.newLong) {
      setValittuMarker(null);
      setViewport({
        latitude: parseFloat(props.newLat),
        longitude: parseFloat(props.newLong),
        zoom: 5,
        transitionDuration: 500,
      });
    }
  }, [props.newLat, props.newLong]);

  const naytaPopUp = (paikka) => {
    return (
      <div className="popup">
        <h3>{paikka.nimi}</h3>
        <p>Tyyppi: {paikka.tyyppi}</p>
        <p>Maakunta: {paikka.maakunta}</p>
        <p>Koordinaatit: {paikka.koordinaatti}</p>
      </div>
    );
  };

  return (
    <ReactMapGl
      className="map"
      {...viewport}
      mapboxApiAccessToken={access_token}
      mapStyle="mapbox://styles/mapbox/streets-v11"
      height={800}
      width={800}
      onViewportChange={(viewport) => {
        setViewport(viewport);
      }}
    >
      {
        paikat.length > 0 &&
          transition.length > 0 &&
          transition((styles, paikka) => {
            let koord = paikka.koordinaatti.split(",");
            return (
              <animated.div key={paikka.nimi} style={styles}>
                <Marker
                  className="markers"
                  latitude={parseFloat(koord[0])}
                  longitude={parseFloat(koord[1])}
                  offsetLeft={-25 / 2}
                  offsetTop={-25 / 2}
                  onClick={() => {
                    setValittuMarker(paikka);
                    let koordinaatit = paikka.koordinaatti.split(",");
                    setLat(parseFloat(koordinaatit[0]));
                    setLong(parseFloat(koordinaatit[1]));
                  }}
                >
                  <img className="marker-images" src={icon}></img>
                </Marker>
              </animated.div>
            );
          })
        /* naytaMarkerit &&
        paikat[0].map((paikka, index) => {
          let koord = paikka.koordinaatti.split(",");
          return (
            <CSSTransition
              in={naytaMarkerit}
              timeout={2000}
              classNames="animated-marker"
              onEnter={console.log("TOIMII")}
            >
              <Marker
                key={index}
                className="markers"
                latitude={parseFloat(koord[0])}
                longitude={parseFloat(koord[1])}
                offsetLeft={-25 / 2}
                offsetTop={-25 / 2}
                onClick={() => {
                  setValittuMarker(paikka);
                  let koordinaatit = paikka.koordinaatti.split(",");
                  setLat(parseFloat(koordinaatit[0]));
                  setLong(parseFloat(koordinaatit[1]));
                }}
              >
                <img className="marker-images" src={icon}></img>
              </Marker>
            </CSSTransition>
          );
        }) */
      }

      {valittuMarker && lat && long && (
        <Popup
          latitude={lat}
          longitude={long}
          anchor="bottom"
          offsetTop={-25 / 2}
          onClose={() => setValittuMarker(null)}
        >
          {naytaPopUp(valittuMarker)}
        </Popup>
      )}
    </ReactMapGl>
  );
}

export default Map;
