import { _adapters } from "chart.js";
import React, { useState, useEffect } from "react";
import { Bar } from "react-chartjs-2";
import { Pie } from "react-chartjs-2";

function BarChart(props) {
  const [tyypit, setTyypit] = useState(props.tyypit);
  const [paikatMaakunnassa, setPaikatMaakunnassa] = useState(
    props.paikatMaakunnassa
  );
  const [tyyppimaarat, setTyyppimaarat] = useState([]);
  const [prosentit, setProsentit] = useState([100]);

  useEffect(() => {
    setTyypit(props.tyypit);
  }, [props.tyypit]);

  useEffect(() => {
    setPaikatMaakunnassa(props.paikatMaakunnassa);
    laskeMaarat();
  }, [props.paikatMaakunnassa]);

  const laskeMaarat = () => {
    let laavut = 0;
    let kodat = 0;
    let paivatuvat = 0;
    let nuotiopaikat = 0;
    let luolat = 0;
    let auotiotuvat = 0;
    let ruokailukatokset = 0;
    let nahtavyydet = 0;
    let lintutornit = 0;
    let varaustuvat = 0;
    let kammit = 0;
    let porokampat = 0;
    let lahteet = 0;
    let maarat = [];

    async function f() {
      for (let paikka of props.paikatMaakunnassa) {
        switch (paikka.tyyppi) {
          case "Laavu":
            laavut++;
            break;
          case "Kota":
            kodat++;
            break;
          case "Päivätupa":
            paivatuvat++;
            break;
          case "Nuotiopaikka":
            nuotiopaikat++;
            break;
          case "Luola":
            luolat++;
            break;
          case "Autiotupa":
            auotiotuvat++;
            break;
          case "Ruokailukatos":
            ruokailukatokset++;
            break;
          case "Nähtävyys":
            nahtavyydet++;
            break;
          case "Lintutorni":
            lintutornit++;
            break;
          case "Varaustupa":
            varaustuvat++;
            break;
          case "Kammi":
            kammit++;
            break;
          case "Porokämppä":
            porokampat++;
            break;
          case "Lähde":
            lahteet++;
            break;

          default:
            break;
        }
      }
    }

    f().then(() => {
      maarat = [
        laavut,
        kodat,
        paivatuvat,
        nuotiopaikat,
        luolat,
        auotiotuvat,
        ruokailukatokset,
        nahtavyydet,
        lintutornit,
        varaustuvat,
        kammit,
        porokampat,
        lahteet,
      ];
      setTyyppimaarat(maarat);
      laskeProsentit(maarat);
    });
  };

  const laskeProsentit = (maarat) => {
    const paikkoja = props.paikatMaakunnassa.length;
    let pros = [];
    async function f() {
      pros = maarat.map((maara) => ((maara / paikkoja) * 100).toFixed());
    }
    console.log("määrät:", maarat);

    f().then(() => {
      let listassaOnDataa = false;
      for (let m of maarat) {
        if (m > 0) {
          listassaOnDataa = true;
        }
      }
      setProsentit(pros);
    });
  };

  console.log("prosentit:", prosentit);

  return (
    <div className="charts-wrapper">
      <div className="barchart-wrapper">
        <Bar
          data={{
            labels: [...tyypit],
            datasets: [
              {
                label: "test",
                data: [...tyyppimaarat],
                backgroundColor: "blue",
              },
            ],
          }}
        ></Bar>

        <div className="piechart-wrapper">
          <Pie
            data={{
              labels: [...tyypit],
              datasets: [
                {
                  label: "test",
                  data: [...prosentit],
                  backgroundColor: "blue",
                },
              ],
            }}
          ></Pie>
        </div>
      </div>
    </div>
  );
}

export default BarChart;
