import React, { useState, useEffect } from "react";
import ReactMapGl from "react-map-gl";
import Valikot from "./Valikot";

const access_token =
  "pk.eyJ1Ijoic2F1bGlrIiwiYSI6ImNrcWM4NXJoYzAwdzkybm56djVka2VzZW0ifQ.QmYDZh6DNEwu5ITY9lQ6Ag";
const API_URL = "https://tulikartta.fi/api-json2.php";
let dropmenuMaakunnatIsVisible = false;
let dropMenuTyypitIsVisible = false;

function App() {
  const [viewport, setViewport] = useState({
    latitude: 62,
    longitude: 25,
    zoom: 5,
  });

  const [paikat, setPaikat] = useState([]);
  const [maakunnat, setMaakunnat] = useState([]);
  const [tyyppi, setTyyppi] = useState([]);

  useEffect(() => {
    fetch(API_URL, {
      "Content-Type": "application/json",
      Accept: "application/json",
    })
      .then((response) => {
        console.log(response);
        return response.json();
      })
      .then((jsonData) => {
        const paikatArr = [...jsonData];
        const maakunnatArr = [];
        const tyypitArr = [];

        for (let i of paikatArr) {
          maakunnatArr.push(i.maakunta);
          tyypitArr.push(i.tyyppi);
        }
        const maakunnatRemoveDuplicates = [...new Set(maakunnatArr)];
        const tyypitRemoveDublicates = [...new Set(tyypitArr)];
        console.log("tyypit:", tyypitRemoveDublicates);

        setPaikat({ ...jsonData });
        setMaakunnat({ ...maakunnatRemoveDuplicates });
      });
  }, []);

  const luoValikot = () => {
    let selectBox = document.getElementById("input");
    const options = Object.values(maakunnat).map((maakunta) => (
      <p
        className="valinnat"
        onClick={(e) => {
          selectBox.value = e.target.innerHTML;
          selectBox.focus();
          document.getElementById("container").style.display = "none";
          dropmenuMaakunnatIsVisible = false;
        }}
      >
        {maakunta}
      </p>
    ));

    return (
      <div
        id={"maakunnatContainer"}
        className="options"
        style={{ display: "none" }}
      >
        {options}
      </div>
    );
  };

  const naytaMaakunnat = () => {
    //document.getElementById("container").style.display = "initial";
    let valikko = document.getElementById("container");

    dropmenuMaakunnatIsVisible = !dropmenuMaakunnatIsVisible;
    if (dropmenuMaakunnatIsVisible) {
      valikko.style.display = "block";
    } else {
      valikko.style.display = "none";
    }
  };

  const naytaTyypit = () => {
    let valikko = document.getElementById("container");

    dropmenuIsVisible = !dropmenuIsVisible;
    if (dropmenuIsVisible) {
      valikko.style.display = "block";
    } else {
      valikko.style.display = "none";
    }
  };

  return (
    <div>
      <input
        id={"inputMaakunnat"}
        type={"text"}
        onClick={() => naytaMaakunnat()}
        readOnly
      ></input>

      {luoValikot()}

      <input
        id={"inputTyypit"}
        type={"text"}
        onClick={() => {
          naytaTyypit();
        }}
        readOnly
      ></input>

      <ReactMapGl
        className="map"
        {...viewport}
        mapboxApiAccessToken={access_token}
        mapStyle="mapbox://styles/mapbox/streets-v11"
        height={800}
        width={600}
        onViewportChange={(viewport) => {
          setViewport(viewport);
        }}
      ></ReactMapGl>
    </div>
  );
}

export default App;
