import { _adapters } from "chart.js";
import React, { useState, useEffect } from "react";
import { Bar } from "react-chartjs-2";

function BarChart(props) {
  const [tyypit, setTyypit] = useState(props.tyypit);
  const [paikatMaakunnassa, setPaikatMaakunnassa] = useState(
    props.paikatMaakunnassa
  );
  const [tyyppimaarat, setTyyppimaarat] = useState({
    laavut: 0,
    kodat: 0,
    paivatuvat: 0,
    nuotiopaikat: 0,
    luolat: 0,
    auotiotuvat: 0,
    ruokailukatokset: 0,
    nahtavyydet: 0,
    lintutornit: 0,
    varaustuvat: 0,
    kammit: 0,
    porokampat: 0,
    lahteet: 0,
  });

  useEffect(() => {
    setTyypit(props.tyypit);
  }, [props.tyypit]);

  useEffect(() => {
    setPaikatMaakunnassa(props.paikatMaakunnassa);
    let laavut = 0;
    let kodat = 0;
    let paivatuvat = 0;
    let nuotiopaikat = 0;
    let luolat = 0;
    let auotiotuvat = 0;
    let ruokailukatokset = 0;
    let nahtavyydet = 0;
    let lintutornit = 0;
    let varaustuvat = 0;
    let kammit = 0;
    let porokampat = 0;
    let lahteet = 0;

    async function f() {
      for (let paikka of paikatMaakunnassa) {
        switch (paikka.tyyppi) {
          case "Laavu":
            laavut++;
            break;
          case "Kota":
            kodat++;
            break;
          case "Päivätupa":
            paivatuvat++;
            break;
          case "Nuotiopaikka":
            nuotiopaikat++;
            break;
          case "Luola":
            luolat++;
            break;
          case "Autiotupa":
            auotiotuvat++;
            break;
          case "Ruokailukatos":
            ruokailukatokset++;
            break;
          case "Nähtävyys":
            nahtavyydet++;
            break;
          case "Lintutorni":
            lintutornit++;
            break;
          case "Varaustupa":
            varaustuvat++;
            break;
          case "Kammi":
            kammit++;
            break;
          case "Porokämppä":
            porokampat++;
            break;
          case "Lähde":
            lahteet++;
            break;

          default:
            break;
        }
      }
    }

    /* setTyyppimaarat({
      laavut: laavut,
      kodat: kodat,
      paivatuvat: paivatuvat,
      nuotiopaikat: nuotiopaikat,
      luolat: luolat,
      auotiotuvat: auotiotuvat,
      ruokailukatokset: ruokailukatokset,
      nahtavyydet: nahtavyydet,
      lintutornit: lintutornit,
      varaustuvat: varaustuvat,
      kammit: kammit,
      porokampat: porokampat,
      lahteet: lahteet,
    }); */
    f().then(() => {
      const maarat = [
        laavut,
        kodat,
        paivatuvat,
        nuotiopaikat,
        luolat,
        auotiotuvat,
        ruokailukatokset,
        nahtavyydet,
        lintutornit,
        varaustuvat,
        kammit,
        porokampat,
        lahteet,
      ];
    });

    console.log("tyyppimäärät:", maarat);
  }, [props.paikatMaakunnassa]);

  const laskeMaarat = () => {
    let laavut = 0;
    let kodat = 0;
    let paivatuvat = 0;
    let nuotiopaikat = 0;
    let luolat = 0;
    let auotiotuvat = 0;
    let ruokailukatokset = 0;
    let nahtavyydet = 0;
    let lintutornit = 0;
    let varaustuvat = 0;
    let kammit = 0;
    let porokampat = 0;
    let lahteet = 0;

    for (let paikka of paikatMaakunnassa) {
      switch (paikka.tyyppi) {
        case "Laavu":
          laavut++;
          break;
        case "Kota":
          kodat++;
          break;
        case "Päivätupa":
          paivatuvat++;
          break;
        case "Nuotiopaikka":
          nuotiopaikat++;
          break;
        case "Luola":
          luolat++;
          break;
        case "Autiotupa":
          auotiotuvat++;
          break;
        case "Ruokailukatos":
          ruokailukatokset++;
          break;
        case "Nähtävyys":
          nahtavyydet++;
          break;
        case "Lintutorni":
          lintutornit++;
          break;
        case "Varaustupa":
          varaustuvat++;
          break;
        case "Kammi":
          kammit++;
          break;
        case "Porokämppä":
          porokampat++;
          break;
        case "Lähde":
          lahteet++;
          break;

        default:
          break;
      }
    }

    /* setTyyppimaarat({
      laavut: laavut,
      kodat: kodat,
      paivatuvat: paivatuvat,
      nuotiopaikat: nuotiopaikat,
      luolat: luolat,
      auotiotuvat: auotiotuvat,
      ruokailukatokset: ruokailukatokset,
      nahtavyydet: nahtavyydet,
      lintutornit: lintutornit,
      varaustuvat: varaustuvat,
      kammit: kammit,
      porokampat: porokampat,
      lahteet: lahteet,
    }); */
    const maarat = [
      laavut,
      kodat,
      paivatuvat,
      nuotiopaikat,
      luolat,
      auotiotuvat,
      ruokailukatokset,
      nahtavyydet,
      lintutornit,
      varaustuvat,
      kammit,
      porokampat,
      lahteet,
    ];

    console.log("tyyppimäärät:", maarat);
  };

  return (
    <div className="barchart-wrapper">
      <Bar
        data={{
          labels: [...tyypit],
          datasets: [
            {
              label: "test",
              data: [],
              backgroundColor: "blue",
            },
          ],
        }}
      ></Bar>
    </div>
  );
}

export default BarChart;
