import React, { useState, useEffect } from "react";
import ReactMapGl from "react-map-gl";
import Valikot from "./Valikot";

const access_token =
  "pk.eyJ1Ijoic2F1bGlrIiwiYSI6ImNrcWM4NXJoYzAwdzkybm56djVka2VzZW0ifQ.QmYDZh6DNEwu5ITY9lQ6Ag";
const API_URL = "https://tulikartta.fi/api-json2.php";

function App() {
  const [viewport, setViewport] = useState({
    latitude: 62,
    longitude: 25,
    zoom: 5,
  });

  const [paikat, setPaikat] = useState([]);
  const [maakunnat, setMaakunnat] = useState([]);

  useEffect(() => {
    fetch(API_URL, {
      "Content-Type": "application/json",
      Accept: "application/json",
    })
      .then((response) => {
        console.log(response);
        return response.json();
      })
      .then((jsonData) => {
        const paikatArr = [...jsonData];
        const maakunnatArr = [];

        for (let i of paikatArr) {
          maakunnatArr.push(i.maakunta);
        }
        const uniqueValues = [...new Set(maakunnatArr)];

        setPaikat({ paikatArr });
        setMaakunnat({ uniqueValues });

        setTimeout(() => {
          console.log("paikat:", paikat);
          console.log("maakunnat:", maakunnat);
        }, 2000);
      });
  }, []);

  const luoValikot = () => {
    //console.log("maakunnat: ", maakunnat);
    const options = maakunnat.maakunnat.map((maakunta) => (
      <option value={maakunta}>{maakunta}</option>
    ));
    console.log(options);

    return <select>{options}</select>;
  };

  return (
    <div>
      {luoValikot()}
      <ReactMapGl
        {...viewport}
        mapboxApiAccessToken={access_token}
        mapStyle="mapbox://styles/mapbox/streets-v11"
        height={800}
        width={600}
        onViewportChange={(viewport) => {
          setViewport(viewport);
        }}
      ></ReactMapGl>
    </div>
  );
}

export default App;
