import { _adapters } from "chart.js";
import React, { useState, useEffect } from "react";
import { Bar } from "react-chartjs-2";

function BarChart(props) {
  const [tyypit, setTyypit] = useState(props.tyypit);
  const [paikatMaakunnassa, setPaikatMaakunnassa] = useState(
    props.paikatMaakunnassa
  );
  const [tyyppimaarat, setTyyppiaarat] = useState({
    laavut: 0,
    kodat: 0,
    paivatuvat: 0,
    nuotiopaikat: 0,
    luolat: 0,
    auotiotuvat: 0,
    ruokailukatokset: 0,
    nahtavyydet: 0,
    lintutornit: 0,
    varaustuvat: 0,
    kammit: 0,
    porokampat: 0,
    lahteet: 0,
  });

  useEffect(() => {
    setTyypit(props.tyypit);
  }, [props.tyypit]);

  useEffect(() => {
    setPaikatMaakunnassa(props.paikatMaakunnassa);
    laskeMaarat();
  }, [props.paikatMaakunnassa]);

  const laskeMaarat = () => {
    let laavut = 0;
    let kodat = 0;
    let paivatuvat = 0;
    let nuotiopaikat = 0;
    let luolat = 0;
    let auotiotuvat = 0;
    let ruokailukatokset = 0;
    let nahtavyydet = 0;
    let lintutornit = 0;
    let varaustuvat = 0;
    let kammit = 0;
    let porokampat = 0;
    let lahteet = 0;

    for (let paikka of paikatMaakunnassa) {
      console.log("paikka loopissa:", paikka);
      switch (paikka.tyyppi) {
        case "laavu":
          console.log("laavu löytyi");
          break;
        case "kota":
          console.log("kota löytyi");
          break;
        default:
          return;
      }
    }
  };

  console.log("barchart tyypit:", tyypit);
  return (
    <div className="barchart-wrapper">
      <Bar
        data={{
          labels: [...tyypit],
          datasets: [
            {
              label: "test",
              data: [1, 2, 3, 4],
              backgroundColor: "blue",
            },
          ],
        }}
      ></Bar>
    </div>
  );
}

export default BarChart;
