import React, { useState, useEffect } from "react";
import Valikot from "./Valikot";
import Map from "./Map";
import BarChart from "./BarChart";
import PieChart from "./PieChart";

const API_URL = "https://tulikartta.fi/api-json2.php";
let dropmenuMaakunnatIsVisible = false;
let dropmenuTyypitIsVisible = false;

function App() {
  const [paikat, setPaikat] = useState([]);
  const [maakunnat, setMaakunnat] = useState([]);
  const [tyypit, setTyypit] = useState([]);
  const [valittuMaakunta, setValittuMaakunta] = useState("");
  const [valittuTyyppi, setValittuTyyppi] = useState("");
  const [rajatutPaikat, setRajatutPaikat] = useState([]);
  const [paikatMaakunnassa, setPaikatMaakunnassa] = useState([]);
  const [newLat, setNewLat] = useState();
  const [newLong, setNewLong] = useState();

  useEffect(() => {
    fetch(API_URL, {
      "Content-Type": "application/json",
      Accept: "application/json",
    })
      .then((response) => {
        console.log(response);
        return response.json();
      })
      .then((jsonData) => {
        const paikatArr = [...jsonData];
        const maakunnatArr = [];
        const tyypitArr = [];

        for (let i of paikatArr) {
          maakunnatArr.push(i.maakunta);
          tyypitArr.push(i.tyyppi);
        }
        const maakunnatRemoveDuplicates = [...new Set(maakunnatArr)];
        const tyypitRemoveDublicates = [...new Set(tyypitArr)];

        setPaikat([...jsonData]);
        setMaakunnat([...maakunnatRemoveDuplicates]);
        setTyypit([...tyypitRemoveDublicates]);
      });
  }, []);

  useEffect(() => {
    const paikatMaakunnassaArr = paikat.filter(
      (paikka) => paikka.maakunta == valittuMaakunta
    );
    setPaikatMaakunnassa([...paikatMaakunnassaArr]);
    /* const splitKoordinaatit = paikatMaakunnassaArr[0].koordinaatti;
    setNewLat(splitKoordinaatit[0]);
    setNewLong(splitKoordinaatit[1]); */
    console.log("koordinaatit:", paikatMaakunnassaArr[0].koordinaatti);
  }, [valittuMaakunta]);

  useEffect(() => {
    paivitaPaikat();
  }, [valittuMaakunta, valittuTyyppi]);

  const naytaMaakunnat = () => {
    //document.getElementById("container").style.display = "initial";
    let valikko = document.getElementById("maakunnatContainer");

    dropmenuMaakunnatIsVisible = !dropmenuMaakunnatIsVisible;
    if (dropmenuMaakunnatIsVisible) {
      valikko.style.display = "block";
    } else {
      valikko.style.display = "none";
    }
  };

  const naytaTyypit = () => {
    let valikko = document.getElementById("tyypitContainer");

    dropmenuTyypitIsVisible = !dropmenuTyypitIsVisible;
    if (dropmenuTyypitIsVisible) {
      valikko.style.display = "block";
    } else {
      valikko.style.display = "none";
    }
  };

  const paivitaPaikat = () => {
    if (valittuMaakunta && valittuTyyppi) {
      let rajatutPaikat = [];
      async function f() {
        {
          rajatutPaikat = paikat.filter(
            (paikka) =>
              paikka.maakunta == valittuMaakunta &&
              paikka.tyyppi == valittuTyyppi
          );
        }
      }
      f().then(setRajatutPaikat([rajatutPaikat]));
    }
  };

  const luoMaakuntaValikko = () => {
    let selectBox = document.getElementById("inputMaakunnat");
    const options = Object.values(maakunnat).map((maakunta, index) => (
      <p
        key={index}
        className="valinnat"
        onClick={(e) => {
          selectBox.value = e.target.innerHTML;
          setValittuMaakunta(e.target.innerHTML);
          selectBox.focus();
          document.getElementById("maakunnatContainer").style.display = "none";
          dropmenuMaakunnatIsVisible = false;
        }}
      >
        {maakunta}
      </p>
    ));

    return (
      <div
        id={"maakunnatContainer"}
        className="options"
        style={{ display: "none" }}
      >
        {options}
      </div>
    );
  };

  const luoTyypitValikko = () => {
    let selectBox = document.getElementById("inputTyypit");
    const options = Object.values(tyypit).map((tyyppi, index) => (
      <p
        key={index}
        className="valinnat"
        onClick={(e) => {
          selectBox.value = e.target.innerHTML;
          setValittuTyyppi(e.target.innerHTML);
          selectBox.focus();
          document.getElementById("tyypitContainer").style.display = "none";
          dropmenuTyypitIsVisible = false;
        }}
      >
        {tyyppi}
      </p>
    ));

    return (
      <div
        id={"tyypitContainer"}
        className="options"
        style={{ display: "none" }}
      >
        {options}
      </div>
    );
  };

  return (
    <div>
      <div>
        <input
          id={"inputMaakunnat"}
          type={"text"}
          value={valittuMaakunta}
          onClick={() => naytaMaakunnat()}
          placeholder={"Valitse maakunta"}
          readOnly
        ></input>

        {luoMaakuntaValikko()}

        <input
          id={"inputTyypit"}
          inputMode={"text"}
          value={valittuTyyppi}
          onClick={() => {
            naytaTyypit();
          }}
          placeholder={"Valitse tyyppi"}
          readOnly
        ></input>

        {luoTyypitValikko()}
      </div>
      {maakunnat.length > 0 && tyypit.length > 0 && paikat && rajatutPaikat && (
        <div className="map-chart-wrapper">
          <Map
            className="map"
            maakunta={valittuMaakunta}
            tyyppi={valittuTyyppi}
            data={[...rajatutPaikat]}
            newLat={newLat}
            newLong={newLong}
          ></Map>
          <div className="chart-wrapper">
            <BarChart
              tyypit={[...tyypit]}
              paikatMaakunnassa={paikatMaakunnassa}
            ></BarChart>

            <PieChart
              paikat={[...tyypit]}
              paikatMaakunnassa={paikatMaakunnassa}
              maakunta={valittuMaakunta}
            ></PieChart>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
